varying vec3 vPosition;

uniform float iThickness;
uniform float iDistortAmount;

// attribute float timeCreation;
uniform float iTimeDelta;

// varying float vInterval;

uniform vec3 iTransform;

void main() {

    vec3 viTransform = iTransform;

    float xNoise = cnoise_1_4(position) * (iDistortAmount/25.0);
    float yNoise = cnoise_1_4(position) * (iDistortAmount/20.0);
    float zNoise = cnoise_1_4(position) * (iDistortAmount/50.0);

